      console.log(`Starting call with peer ${peerId}`);

      // Create peer connection if it doesn't exist
      let peerConnection = this.peerConnections.get(peerId);
      if (!peerConnection) {
        console.log(`Creating new peer connection for ${peerId} to start call`);
        peerConnection = this._createPeerConnection(peerId, true);
      } else {
        // If connection exists but we need to initiate a call
        console.log(`Using existing peer connection for ${peerId} to start call`);
        this._createAndSendOffer(peerId);
      }
      return peerConnection;
    } catch (error) {
      console.error('Error starting call:', error);
      if (this.onError) {
        this.onError('Failed to start call. Please try again.');
      }
      throw error;
    }
  }

  /**
   * Handle an answer from a peer
